      double[][] centerX=APrime(tmp1,1,28);
      double[][] keyWidth=new double[28][1];
      for (int i=0; i<27; i++){
         keyWidth[i][0]=input[0][1];}
      keyWidth[27][0]=1.5*input[0][1];
      keyWidth[26][0]=5*input[0][1];

      double[] hubL={2.5, (centerY[0][0]+centerY[1][0])/2};
      double[] hubR={7.5, (centerY[0][0]+centerY[1][0])/2};
      double W=Math.min(input[0][0], input[0][2]);
