   public static double pow2(double x){
      return Math.pow(x,2);
   }

   public static double log2(double x){
      return Math.log(x) / Math.log(2);
   }

   public static double[][] NumArr(double[][] A ,double n, int a, int b) {
      double[][] output=new double[a][b];
      for (int i=0; i<a; i++){
         for (int j=0; j<b; j++){
            output[i][j]=n*A[i][j];
         }}
      return output;
   }


   public static double[][] FixNum(double n, int a, int b) {
      double[][] output=new double[a][b];
      for (int i=0; i<a; i++){
         for (int j=0; j<b; j++){
            output[i][j]=n;
         }}
      return output;
   }


   public static double[][] APrime(double[][] A, int a, int b) {
      double[][] output=new double[b][a];
      for (int i=0; i<a; i++){
         for (int j=0; j<b; j++){
            output[j][i]=A[i][j];
         }}
      return output;
   }


   public static double[][] AB(double[][] A, double[][] B, int a, int b, int c) {
      double[][] output=new double[a][b];
      for (int i=0; i<a; i++){
         for (int k=0; k<c; k++){
            for (int j=0; j<b; j++){
               output[i][j]=A[i][k]*B[k][j];
            }}}
      return output;
   }

